def is_remote(x: item):
    return is_remote_control(x)

def is_dvd_remote(x: item):
    return is_remote(x)

behavior locate_remote():
    body:
        foreach r: item:
            if is_dvd_remote(r) and not visited(r):
                observe(r, "Check if this is the remote control for the DVD player")

behavior __goal__():
    body:
        locate_remote()
