def has_clothes_shirt(x: item):
    # Check if there is a clothes shirt in the available items
    return is_clothes_shirt(x)

behavior __goal__():
    body:
        if exists shirt: item : has_clothes_shirt(shirt) and dirty(shirt):
            # If there is a shirt that needs to be cleaned
            bind shirt: item where:
                has_clothes_shirt(shirt) and dirty(shirt)
            # Move closer to the shirt that needs to be cleaned
            achieve close_char(char, shirt)

        else:
            # Otherwise, observe the environment to find a shirt that needs cleaning
            foreach shirt: item:
                if has_clothes_shirt(shirt) and not visited(shirt):
                    observe(shirt, "Check if the shirt is dirty")
