def is_remote_control_instance(item: item):
    return is_remote_control(item)

def is_television_instance(item: item):
    return is_television(item)

behavior find_remote_control_or_tv():
    body:
        if exists remote_control: item : is_remote_control_instance(remote_control) and not visited(
remote_control):
            bind rc: item where:
                is_remote_control_instance(rc) and not visited(rc)
            observe(rc, "check if it is the remote control")
            achieve close_char(char, rc)

        if exists television: item : is_television_instance(television) and not visited(television):
            bind tv: item where:
                is_television_instance(tv) and not visited(tv)
            observe(tv, "check this television control interface")
            achieve close_char(char, tv)

behavior __goal__():
    body:
        find_remote_control_or_tv()
