def is_book_found(book: item):
    # Function to determine if a book has been found
    return visited(book)

behavior __goal__():
    body:
        if exists book: item : is_book(book) and is_book_found(book):
            # If a book has been found, achieve close proximity
            bind book: item where:
                is_book(book) and is_book_found(book)
            achieve close_char(char, book)

        else:
            # If no book has been found, inspect all books
            foreach book: item:
                if is_book(book) and not visited(book):
                    observe(book, "Check if this is the book needed")
